export interface Aria2nOptions {
    host?: string;
    port?: number;
    path?: string;
    secure?: boolean;
    secret?: string;
}

export type Aria2Version = {
    enabledFeatures: string[];
    version: string;
};

export type Status = {
    bitfield: string;
    completedLength: string;
    connections: string;
    dir: string;
    downloadSpeed: string;
    files: File[];
    gid: string;
    numPieces: string;
    pieceLength: string;
    status: string;
    totalLength: string;
    uploadLength: string;
    uploadSpeed: string;
    bitTorrent: Bittorrent;
    // todo)) add more fields regarding torrents
};

export type File = {
    completedLength: string;
    index: string;
    length: string;
    path: string;
    selected: string;
    uris: Uri[];
};

export type Uri = {
    status: string;
    uri: string;
};

export type Bittorrent = {
    announceList: string[][];
    mode: string;
    info: { name: string };
};

export type Key =
    | 'bitfield'
    | 'completedLength'
    | 'connections'
    | 'dir'
    | 'downloadSpeed'
    | 'files'
    | 'gid'
    | 'numPieces'
    | 'pieceLength'
    | 'status'
    | 'totalLength'
    | 'uploadLength'
    | 'uploadSpeed';

